import React, { Component } from "react";
import {
  BrowserRouter as Router,
  Route,
  Switch,
  Link,
  Redirect
} from "react-router-dom";
import "bootstrap/dist/css/bootstrap.min.css";
import "font-awesome/css/font-awesome.min.css";


import Account from ".././../Account";

export class Login extends React.Component {
  constructor(){
    super();
    this.state={ 
      username:'admin', 
      password:'admin',
      jwt: ''
    }
  }
  handleChange = event =>{
    this.setState({ [event.target.name]:event.target.value })
  }
  handleSubmit = event =>{
    event.preventDefault();
    console.log('username: ' + this.state.username)
    console.log('password: ' + this.state.password)
  //  console.log('token: ' + this.state.jwt);

    const url ='http://localhost:8080/authenticate'

    const data = {
      username:this.state.username,
      password:this.state.password
    }

  fetch(url, { 
      method: 'POST',
      body: JSON.stringify(data),
      headers:{ 
        //'Access-Control-Allow-Origin: *'
        'Content-Type': 'application/json'
       // 'Content-Type': 'no-core'
      } 
    })

    .then(res => {
      // code to handle the response data
       // 200-299
      if(res.ok){
       const jwt = res.text();  //parse the response to JSON
       this.setState({ jwt });
        console.log(this.state.jwt)
      //  localStorage.setItem("token", data.jwt)
      }
    })
   // .then(jwt => console.log(jwt))
   .then(response => console.log('Success:', response))
    .catch(error => console.error('Error:', error));
   
  }
  

  render(){
    return(
      <div>
        <form  onSubmit={this.handleSubmit}>
          <ul>
            <input type='text' placeholder = 'username' name='username' onChange={this.handleChange} />
            <input type='text' placeholder = 'password' name='password' onChange={this.handleChange} />
            <p></p>
            <input type='submit' value='Log In' /> 
          </ul>
        </form> 
      </div>
      
    )}
      

}


class Connect extends Component {
  render() {
    return (
      <div className="Connect">
        <Router>
          <>
            <Switch>
             <Route path="/welcome/:name" component={WelcomeComponent} />  
              <Route path="/" exact component={Login} />
              <Route path="/login" component={Login} />
              <Route path="/accounts" component={AccountComponent} />

             
            
            
            </Switch>
          </>
        </Router>
        {/*<WelcomeComponent />
        <LoginComponent />*/}
      </div>
    );
  }
}



class AccountComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      account: []
    };
  }
  render() {
    return (
      <div>
        <a href="/logout">logout</a>
        <h4>Account Information</h4>
        <table>
          <thead>
            <tr>
              <p>
                <Link to="/checking">Checking Accounts</Link>
              </p>
              <p>
                <Link to="/savings">Savings Account</Link>
              </p>
              <p>
                <Link to="/cd">Certificate of Deposit Account (CD)</Link>
              </p>
              <Link to="/ira">Individual Retirement Accounts (IRA)</Link>
            </tr>
          </thead>
          <tbody>
            <tr />
          </tbody>
        </table>
      </div>
    );
  }
}

class LogoutComponent extends Component {
  render() {
    return (
      <>
        <h4>You are logged out</h4>
        <p>Thank You for Using Our Application.</p>
      </>
    );
  }
}

class WelcomeComponent extends Component {
  render() {
    return (
      <>
        <Link to="/logout">logout</Link>
        <h4>Welcome!</h4>
        <div>
          {" "}
          Welcome {this.props.match.params.name}. Click here to view your
          Account Information <a href="/account">Go to my account</a>
        </div>
        <div className="container">
          <div>
            <table>
              <thead>
                <tr />
              </thead>
              <tbody>
                <tr />
              </tbody>
            </table>
          </div>
          This is here for testing
          <button
            onClick={this.retrieveWelcomeMessage}
            className="btn btn-success"
          >
            Get Welcome Message
          </button>
        </div>
      </>
    );
  }
}

function ErrorComponent() {
  return <div>An Error Occurred.</div>;
}


export default Connect;
