import React, { Component } from "react";
import Checkings from '../Pages/Checkings';
import Savings from '../Pages/Savings';
import PersonalCheckings from '../Pages/PersonalCheckings';
import DBA from '../Pages/DBA';
import CD from '../Pages/CD';
import IRA from '../Pages/IRA';
import Transfer from "../Pages/Transfer";
import { Button, Flex, Box } from 'rebass'

import { Card } from "reactstrap";
class User extends Component 
{
  constructor()
  {
    super();
    this.state={ 
      firstName:"",
      lastName:"",
      fetchedData:[],
      CheckingsPage: false,
      SavingsPage: false,
      PersonalCheckingsPage: false,
      DBAPage: false,
      CDPage: false,
      IRAPage: false,
      TransferPage: false
    }
    this.hideComponent = this.hideComponent.bind(this);
  }

  hideComponent(name) {
    this.setState({
      CheckingsPage: false,
      SavingsPage: false,
      PersonalCheckingsPage: false,
      DBAPage: false,
      CDPage: false,
      IRAPage: false,
      TransferPage: false
    });
    switch (name) {
      case "CheckingsPage":
        this.setState({ CheckingsPage: !this.state.CheckingsPage });
        break;
      case "SavingsPage":
        this.setState({ SavingsPage: !this.state.SavingsPage });
        break;
      case "PersonalCheckingsPage":
        this.setState({ PersonalCheckingsPage: !this.state.PersonalCheckingsPage });
        break;
      case "DBAPage":
        this.setState({ DBAPage: !this.state.DBAPage });
        break;
      case "CDPage":
        this.setState({ CDPage: !this.state.CDPage });
        break;
      case "IRAPage":
        this.setState({ IRAPage: !this.state.IRAPage });
        break;
      case "TransferPage":
        this.setState({ TransferPage: !this.state.TransferPage });
        break;
      //default:
        //null;
    }
  }
  render() 
  {
    const { CheckingsPage, SavingsPage, PersonalCheckingsPage, DBAPage, CDPage, IRAPage, TransferPage} = this.state;
    const logoutUrl = "http://localhost:3000/#/";
    const transferUrl = "http://localhost:3000/#/Transfer";
    const url = "";
    return (
      <div>
       <h1>Hello</h1>


       import React from 'react'
import { Box, Flex } from 'rebass'

export default props =>
  <Flex
    sx={{
      flexDirection: 'column',
      minHeight: '100vh'
    }}>
    <Box
      sx={{
        display: 'grid',
        flex: 1,
        minHeight: '100vh',
        gridTemplateAreas: [
          '"long-box long-box" "left-box right-box" "wide-box wide-box"',
          '"long-box long-box left-box right-box" "long-box long-box wide-box wide-box"'
        ],
        gridTemplateColumns: [
          'repeat(2, 1fr)',
          'repeat(4, 1fr)'
        ],
        gridTemplateRows: [
          '2fr 1fr 1fr',
          'none'
        ],
        gridGap: 20,
        margin: 20
      }}>
      <Box
        sx={{
          flex: 1,
          gridArea: 'long-box'
        }}>
        Long Box
      </Box>
      <Box
        sx={{
          flex: 1,
          gridArea: 'left-box'
        }}>
         {CheckingsPage && <Checkings />}
            {SavingsPage && <Savings />}
            {PersonalCheckingsPage && <PersonalCheckings />}
            {DBAPage && <DBA />}
            {CDPage && <CD />}
            {IRAPage && <IRA />}
            {TransferPage && <Transfer />}
      </Box>
      <Box
        sx={{
          flex: 1,
          gridArea: 'right-box'
        }}>
        Box Right
      </Box>
      <Box
        sx={{
          flex: 1,
          gridArea: 'wide-box'
        }}>
      <button className = 'button' onClick={() => this.hideComponent("SavingsPage")}>My Savings Account</button>
    <button className = 'button' onClick={() => this.hideComponent("CheckingsPage")}>My Checkings Account</button>
    <button className = 'button' onClick={() => this.hideComponent("DBAPage")}>My DBA Account</button>
    <button className = 'button' onClick={() => this.hideComponent("CDPage")}>My CD Account</button>
    <button className = 'button' onClick={() => this.hideComponent("IRAPage")}>My IRA Accounts</button>
    <button className = 'button' onClick={() => this.hideComponent("TransferPage")}>Transaction</button>
    
    <button className = 'button' onClick={() => this.hideComponent("PersonalCheckingsPage")}>Settings</button>
    <a href={logoutUrl} className = "logoutBtn">
      <p>
        <button className = 'button' >LogOut</button>
      </p>
    </a>
      </Box>
    </Box>
  </Flex>










       </div>
    );
  }
}

