import React, { Component } from "react";
import {
  BrowserRouter as Router,
  Route,
  Switch,
  Link,
  Redirect
} from "react-router-dom";

import "bootstrap/dist/css/bootstrap.min.css";
import "font-awesome/css/font-awesome.min.css";


export class Login extends React.Component {
  constructor(props){
    super(props);
    this.state={ 
      username:'admin', 
      password:'admin',
      jwt: ''
    }
  }
  handleChange = event =>{
    this.setState({ [event.target.name]:event.target.value })
  }
  handleSubmit = event =>{
    event.preventDefault();
     console.log('username: ' + this.state.username)
     console.log('password: ' + this.state.password)
  
     const url ='/authenticate'

    const data = {
      username:this.state.username,
      password:this.state.password
    }
    
   
    
    // this.props.history.push('account');
    
  // fetch(url, { 
  //     method: 'POST',
    
  //     headers:{ 
  //       'Content-Type': 'application/json'
  //     } ,
  //     "body": JSON.stringify(data),
  //   })
  //   .then(response => response.json())
  //   .then(response => {
  //     console.log('status '+ JSON.status)
  //     // console.log('status '+ JSON.stringify(response))
    
  //     if(response.ok){
  //     const jwt = JSON.stringify(response);
  //     localStorage.setItem('JwTToken', jwt)
  //  //   localStorage.getItem('JwTToken')
  //     }
  //     this.props.history.push("Account");
  //   })
  //   .catch(err => {
  //     console.log(err);
  //   })
   
  fetch(url, {
    "method": "POST",
    "headers": {
      "content-type": "application/json"
      //"accept": "application/json"
    },
    "body": JSON.stringify(data),
  })
  .then(response => response.json())
  .then(response => {
    //check if we get a 200 ok status
    console.log('status '+ JSON.stringify(response))

    //if(response.ok){
      console.log("Test");
      this.setState({
        jwt: JSON.stringify(response)

      })
      const jwtToken = response.jwt;

      localStorage.setItem('JWTToken', jwtToken)
     // window.location.href= "/account";
     window.location.replace("/account")
   // }
   
    
   // this.props.history.push('/account');
    //this.context.history.push('/Account');
  })
  .catch(err => {
    console.log(err);
  });
   
}

  
 

  render(){
    return(
  
      <div>
        <form  onSubmit={this.handleSubmit}>
          <div>
        
          
          <h1><b>Welcome to Merit Online Banking!</b></h1>
        </div>
          <ul>
            <input type='text' placeholder = 'username' name='username' onChange={this.handleChange} />
            <input type='text' placeholder = 'password' name='password' onChange={this.handleChange} />
            <button onClick={this.handleSubmit}>Login</button>
          </ul>
        
        </form> 
      </div>
      
     
      );
     
      }

    }
// <input type='submit' value='Log In' />
//
// <input type='submit' value='Log In' />
//  <p > {this.state.jwt } </p>


class Connect extends Component {
  render() {
    return (
      <div className="Connect">
        <Router>
          <>
            <Switch>
              <Route path="/welcome/:name" component={WelcomeComponent} />
              <Route path="/" exact component={LoginComponent} />
              <Route path="/login" component={LoginComponent} />
              <Route path="/logout" component={LogoutComponent} />
              <Route path="/accounts" component={AccountComponent} />
              <Route component={ErrorComponent} />
            </Switch>
          </>
        </Router>
        {/*<WelcomeComponent />
        <LoginComponent />*/}
      </div>
    );
  }
}


class AccountComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      account: []
    };
  }
  render() {
    return (
      <div>
        <a href="/logout">logout</a>
        <h4>Account Information</h4>
        <table>
          <thead>
            <tr>
              <p>
                <Link to="/checking">Checking Accounts</Link>
              </p>
              <p>
                <Link to="/savings">Savings Account</Link>
              </p>
              <p>
                <Link to="/cd">Certificate of Deposit Account (CD)</Link>
              </p>
              <Link to="/ira">Individual Retirement Accounts (IRA)</Link>
            </tr>
          </thead>
          <tbody>
            <tr />
          </tbody>
        </table>
      </div>
    );
  }
}

class LogoutComponent extends Component {
  render() {
    return (
      <>
        <h4>You are logged out</h4>
        <p>Thank You for Using Our Application.</p>
      </>
    );
  }
}

class WelcomeComponent extends Component {
  render() {
    return (
      <>
        <Link to="/logout">logout</Link>
        <h4>Welcome!</h4>
        <div>
          {" "}
          Welcome {this.props.match.params.name}. Click here to view your
          Account Information <a href="/account">Go to my account</a>
        </div>
        <div className="container">
          <div>
            <table>
              <thead>
                <tr />
              </thead>
              <tbody>
                <tr />
              </tbody>
            </table>
          </div>
          This is here for testing
          <button
            onClick={this.retrieveWelcomeMessage}
            className="btn btn-success"
          >
            Get Welcome Message
          </button>
        </div>
      </>
    );
  }
}

function ErrorComponent() {
  return <div>An Error Occurred.</div>;
}

class LoginComponent extends Component {
  constructor(props) {
    super(props);

    this.state = {
      username: "",
      password: "",
      hasLoginFailed: false,
      showSuccessMessage: true
    };

    /*this.handleUsernameChange = this.handleUsernameChange.bind(this);
    this.handlePasswordChange = this.handlePasswordChange.bind(this);*/
    this.handleChange = this.handleChange.bind(this);
    this.loginClicked = this.loginClicked.bind(this);
  }

  handleChange(event) {
    //console.log(this.state);
    this.setState({
      [event.target.name]: event.target.value
    });
  }
  /* handleUsernameChange(event) {
    console.log(event.target.value);
    this.setState(
      { 
        [event.target.name] 
