import React, { Component } from "react";
import {
  BrowserRouter as Router,
  Route,
  Switch,
  Link,
  Redirect
} from "react-router-dom";

import "bootstrap/dist/css/bootstrap.min.css";
import "font-awesome/css/font-awesome.min.css";


export class Login extends React.Component {
  constructor(props){
    super(props);
    this.state={ 
      username:'admin', 
      password:'admin',
      jwt: ''
    }
  }
  handleChange = event =>{
    this.setState({ [event.target.name]:event.target.value })
  }
  handleSubmit = event =>{
    event.preventDefault();
     console.log('username: ' + this.state.username)
     console.log('password: ' + this.state.password)
  
     const url ='http://localhost:8080/authenticate'

    const data = {
      username:this.state.username,
      password:this.state.password
    }
    
   
    
    // this.props.history.push('account');
    
  // fetch(url, { 
  //     method: 'POST',
    
  //     headers:{ 
  //       'Content-Type': 'application/json'
  //     } ,
  //     "body": JSON.stringify(data),
  //   })
  //   .then(response => response.json())
  //   .then(response => {
  //     console.log('status '+ JSON.status)
  //     // console.log('status '+ JSON.stringify(response))
    
  //     if(response.ok){
  //     const jwt = JSON.stringify(response);
  //     localStorage.setItem('JwTToken', jwt)
  //  //   localStorage.getItem('JwTToken')
  //     }
  //     this.props.history.push("Account");
  //   })
  //   .catch(err => {
  //     console.log(err);
  //   })
   
  fetch(url, {
    "method": "POST",
    "headers": {
      "content-type": "application/json"
      //"accept": "application/json"
    },
    "body": JSON.stringify(data),
  })
  .then(response => response.json())
  .then(response => {
    //check if we get a 200 ok status
    console.log('status '+ JSON.stringify(response))
    if(response.ok){
    }
    this.setState({
      jwt: JSON.stringify(response)
    })
    const jwtToken = JSON.stringify(response);
    localStorage.setItem('JWTToken', jwtToken)
  
   

    // this.props.history.push('/account');
   //this.context.history.push('/Account');
  })
  .catch(err => {
    console.log(err);
  });
   
}

  
 

  render(){
    return(
  
      <div>
        <form  onSubmit={this.handleSubmit}>
          <ul>
            <input type='text' placeholder = 'username' name='username' onChange={this.handleChange} />
            <input type='text' placeholder = 'password' name='password' onChange={this.handleChange} />
            <button onClick={this.handleSubmit}>Login</button>
          </ul>
        
        </form> 
      </div>
      
     
      );
     
      }

    }
// <input type='submit' value='Log In' />
