import React, { Component } from "react";
import {
    Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle, Button, ListGroup, ListGroupItem
  } from 'reactstrap';


  
class Savings extends Component {
  constructor(){
    super();
    this.state={ 
      balance : '',
      interestRate : ''
     
    }
    this.Cancel = this.Cancel.bind(this);
  }
  handleChange = event =>{
    this.setState({ [event.target.name]:event.target.value })
  }
  handleSubmit = event =>{
    event.preventDefault();
    const url ='http://localhost:9090/Me/SavingsAccount'

    const data = {
      balance:this.state.balance,
      interestRate:this.state.interestRate
      
    }
  fetch(url, { 
      method: 'POST',
      body: JSON.stringify(data),
      headers:{ 
        'Content-Type': 'application/json' ,
        'Authorization': `Bearer ${ localStorage.getItem("JWTToken")}`
      } ,
      "body": JSON.stringify(data),
    })
    .then(body => body.json())
    .then((body) => {
      console.log('status '+JSON.stringify(body))
      const c = JSON.stringify(body);
      this.setState({
        myResponse:c
      })
    });
    this.props.history.push("User");
    this.Cancel();
  }
  Cancel() {
   // this.props.history.push("");
  }
  
  render() {
    return (
     <div>

      <form onSubmit={this.handleSubmit}>
          <h3>My Savings </h3>
          <ListGroup className = 'card'>
            <input className = 'input' type='number' placeholder = 'Amount' name='balance' onChange={this.handleChange} />
            <input className = 'input' type='number' placeholder = 'Rate ' name='Rate' onChange={this.handleChange} />
            <input className = 'button' type='submit' value='Submit' /> 
            </ListGroup>
          <p className = 'summary'> ... {this.state.myResponse}</p>
        </form> 

     </div>
    );
  }
}
export default Savings;
